import React from 'react'
import Head from 'next/head';
import ListingWrapper, {
    CheckVisaWrapper,
    BreadcrumbWrapper
} from 'container/Listing/Listing.style';
import { Select, Breadcrumb, Divider } from 'antd';
import CheckVisaItem from 'components/CheckVisa/CheckVisa';

const { Option } = Select;

const children = [];

function onChange(value) {
    console.log(`selected ${value}`);
}

function onBlur() {
    console.log('blur');
}

function onFocus() {
    console.log('focus');
}

function onSearch(val) {
    console.log('search:', val);
}

function handleChange(value) {
    console.log(`selected ${value}`);
}

export default function CheckVisa() {

    const countries = [
        { name: 'Hàn Quốc (Korea)' },
        { name: 'Nhật Bản (Janpan)' },
        { name: 'Đài Loan (Taiwan)' },
        { name: 'Trung Quốc (China)' },
        { name: 'Hồng Kông (Hongkong)' },
        { name: 'Ma Cao (Macau)' },
        { name: 'Nga (Russia)' },
        { name: 'Châu âu (Schengen)' },
        { name: 'Úc (Australia)' },
        { name: 'New Zealand' },
        { name: 'Canada' },
        { name: 'Mỹ (America)' },
        { name: 'Ấn độ (India)' },
        { name: 'Dubai AUE' }
    ];

    const mucdich = [
        { name: 'Du Lịch' },
        { name: 'Công tác' },
        { name: 'Du học' },
        { name: 'Thăm thân' },
        { name: 'Lao động' },
        { name: 'Hội chợ - Triển lãm' },
        { name: 'Định cư' }
    ];

    const congviec = [
        { name: 'Không có việc làm / Thất nghiệp' },
        { name: 'Kinh doanh tự do có chứng minh thu nhập' },
        { name: 'Kinh doanh tự do không có chứng minh thu nhập' },
        { name: 'Nội trợ / Làm việc tự do' },
        { name: 'Lao động có HĐ - Không có BHXH' },
        { name: 'Lao động có HĐ - Có BHXH' },
        { name: 'Nhân viên Nhà Nước' },
        { name: 'Giáo viên / Bác sĩ / Bằng cấp chuyên môn cao' },
        { name: 'Chủ doanh nghiệp có khai thuế' },
        { name: 'Chủ doanh nghiệp Không khai thuế' },
        { name: 'Học sinh / Sinh viên' }
    ];

    const nhanthan = [
        { name: 'Đã kết hôn' },
        { name: 'Đã ly hôn' },
        { name: 'Ly hôn có nuôi con nhỏ' },
        { name: 'Cha / mẹ đơn thân' },
        { name: 'Góa chồng / vợ' },
        { name: 'Về hưu - Có lương hưu' },
        { name: 'Về hưu - không có lương hưu' },
        { name: 'Độc thân' },
        { name: 'Độc thân trên 40 tuổi' },
        { name: 'Độc thân từ 30 tuổi đến 40 tuổi' },
        { name: 'Độc thân từ 22 tuổi đến 29 tuổi' },
        { name: 'Độc thân từ 18 tuổi đến 21 tuổi' },
        { name: 'Độc thân tcòn nhỏ' },
    ];

    const taichinh = [
        { name: 'Sổ tiết kiệm và bảng kê nguồn gốc thu nhập' },
        { name: 'Có sổ tiết kiệm không có bảng kê thu nhập' },
        { name: 'Sổ tiết kiệm trên 500 triệu' },
        { name: 'Sổ tiết kiệm dưới 500 triệu' },
        { name: 'Không có sổ tiết kiệm' }
    ];

    const taisan = [
        { name : 'Giấy tờ nhà đất để ở' },
        { name : 'Giấy tờ sở hữu xe' }
    ];
    return (
        <ListingWrapper>
            <Head>
                <title>Kiểm tra tỉ lệ đậu visa</title>
            </Head>
            <BreadcrumbWrapper>
                <Breadcrumb>
                    <Breadcrumb.Item href="/">
                        <span>Trang chủ</span>
                    </Breadcrumb.Item>
                    <Breadcrumb.Item href="/blog">
                        <span>Kiểm tra tỉ lệ đậu visa</span>
                    </Breadcrumb.Item>
                </Breadcrumb>
                <Divider />
            </BreadcrumbWrapper>
            <CheckVisaWrapper>
                <CheckVisaItem
                    question='1. Chọn Quốc Gia muốn xin visa : '

                >
                    <Select
                        showSearch
                        style={{ width: 200 }}
                        placeholder="Chọn Quốc Gia muốn xin visa"
                        optionFilterProp="children"
                        onChange={onChange}
                        onFocus={onFocus}
                        onBlur={onBlur}
                        onSearch={onSearch}
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {countries.map(item => <Option value={item.name}>{item.name}</Option>)})
                    </Select>
                </CheckVisaItem>
                <CheckVisaItem
                    question='2. Mục đích xin visa: '
                >
                    <Select
                        showSearch
                        style={{ width: 200 }}
                        placeholder="Chọn mục đích"
                        optionFilterProp="children"
                        onChange={onChange}
                        onFocus={onFocus}
                        onBlur={onBlur}
                        onSearch={onSearch}
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {mucdich.map(item => <Option value={item.name}>{item.name}</Option>)}
                    </Select>
                </CheckVisaItem>
                <CheckVisaItem
                    question='3. Thông tin về công việc : '
                >
                    <Select
                        showSearch
                        style={{ width: 350 }}
                        placeholder="Công việc hiện tại"
                        optionFilterProp="children"
                        onChange={onChange}
                        onFocus={onFocus}
                        onBlur={onBlur}
                        onSearch={onSearch}
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {congviec.map(item => <Option value={item.name}>{item.name}</Option>)}
                    </Select>
                </CheckVisaItem>
                <CheckVisaItem
                    question='4. Tình trạng hôn nhân : '
                >
                    <Select
                        showSearch
                        style={{ width: 350 }}
                        placeholder="Chọn tình trạng hôn nhân"
                        optionFilterProp="children"
                        onChange={onChange}
                        onFocus={onFocus}
                        onBlur={onBlur}
                        onSearch={onSearch}
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {nhanthan.map(item => <Option value={item.name}>{item.name}</Option>)}
                    </Select>
                </CheckVisaItem>
                <CheckVisaItem
                    question='5. Thông tin về khả năng tài chính : '
                >
                    <Select
                        showSearch
                        style={{ width: 350 }}
                        placeholder="Chọn mức tài chính"
                        optionFilterProp="children"
                        onChange={onChange}
                        onFocus={onFocus}
                        onBlur={onBlur}
                        onSearch={onSearch}
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        {taichinh.map(item => <Option value={item.name}>{item.name}</Option>)}
                    </Select>
                </CheckVisaItem>
                <CheckVisaItem
                    question='6. Tài sản cố định : '
                >
                    <Select
                        mode="multiple"
                        style={{ width: 350 }}
                        placeholder="Chọn tài sản cố định"
                        defaultValue={['china']}
                        onChange={handleChange}
                        optionLabelProp="label"
                    >
                        <Option value="china" label="China">
                            <div className="demo-option-label-item">
                                Giấy tờ nhà đất ở
                            </div>
                        </Option>
                        <Option value="usa" label="USA">
                            <div className="demo-option-label-item">
                                Giấy tờ sở hữu xe ô tô
                            </div>
                        </Option>
                        <Option value="japan" label="Japan">
                            <div className="demo-option-label-item">
                                Không có tài sản cố định
                            </div>
                        </Option>
                    </Select>
                </CheckVisaItem>
            </CheckVisaWrapper>
        </ListingWrapper>
    )
}
