import React from 'react';
import BlogItem from '../BlogItem/BlogItem';
import { RiShareForwardLine , RiChat1Line, RiHeartLine } from "react-icons/ri";

const actionIcon = [
    {
        icon: <RiHeartLine size="18" key = "like"/>,
        
    }, 
    {
        icon: <RiChat1Line size="18" key ='Comment'/>,
        
    },
    {
        icon: <RiShareForwardLine size="18" key= 'Share'/>,
    }
];

export default function BlogCard({
    title,
}) {
    return (
        <BlogItem
            title={title}
            description={'đây là phần mô tả của bài viết dài khoảng 20 từ....'}
            icon={
                actionIcon.map(item => item.icon)
            }
        >
            <img
                alt="logo"
                src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
            />
        </BlogItem>
    );
}
