import React from 'react';
import Link from 'next/link';
import Carousel from 'react-multi-carousel';
import Favourite from 'components/UI/Favorite/Favorite';
import { RiTimeLine, RiBuilding4Line, RiFocusLine } from "react-icons/ri";
import { Button , Card, Avatar } from 'antd';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';

// import TourItem from '../TourItem/TourItem';
const { Meta } = Card;

const responsive = {
    desktop: {
        breakpoint: {
            max: 3000,
            min: 1024,
        },
        items: 1,
        paritialVisibilityGutter: 40,
    },
    mobile: {
        breakpoint: {
            max: 464,
            min: 0,
        },
        items: 1,
        paritialVisibilityGutter: 30,
    },
    tablet: {
        breakpoint: {
            max: 1024,
            min: 464,
        },
        items: 1,
        paritialVisibilityGutter: 30,
    },
};

const tourinfo = [
    {
        name: 'Phương tiện',
        icon: <RiFocusLine color="black" />,
        detail: 'Vietnam airline'
    },
    {
        name: 'Khách sạn',
        icon: <RiBuilding4Line color="black" />,
        detail: 'Adora Hotel 4****'
    },
    {
        name: 'Thời gian',
        icon: <RiTimeLine color="black" />,
        detail: '4 Ngày 3 Đêm'
    }
];

export default function BlogCard({
    location,
    title,
    price,
    gallery,
    deviceType
}) {
    return (
        <Card
            style={{ width: 300 }}
            cover={
                <img
                    alt="example"
                    src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                />
            }
            actions={[
                <SettingOutlined key="setting" />,
                <EditOutlined key="edit" />,
                <EllipsisOutlined key="ellipsis" />,
            ]}
        >
            <Meta
                avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                title="Card title"
                description="This is the description"
            />
        </Card>
    );
}
